import React, { useState}  from "react";
import '../../index.css';

function Form(props) {

    const [newCurrentSavings, setNewCurrentSavings] = useState('');
    const [newDuration, setNewDuration] = useState('');
    const [newInterest, setNewInterest] = useState('');
    const [newYearlyContributions, setNewYearlyContributions] = useState('');

    const currentSavingsInputHandler = (event) => {
        setNewCurrentSavings(event.target.value);
    }
    const yearSavingsInputHandler = (event) => {
        setNewYearlyContributions(event.target.value);
    }
    const durationInputHandler = event => {
        setNewDuration(event.target.value);
    }
    const interestInputHandler = event => {
        setNewInterest(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();
        
      };

      const resetHandler= () => {
        console.log('Clicked!');
        setNewCurrentSavings('');
        setNewDuration('');
        setNewInterest('');
        setNewYearlyContributions('');
    
      }

    
    return (
        <form onSubmit={submitHandler} className="form">
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={currentSavingsInputHandler} value={newCurrentSavings}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={yearSavingsInputHandler} value={newYearlyContributions} />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange={interestInputHandler} value={newInterest}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={durationInputHandler} value={newDuration}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt" onClick={resetHandler}>
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    )
}

export default Form;
