import React, { useState}  from "react";
import '../../index.css';

function Form(props) {

    const [enteredCurrentSavings, setEnteredCurrentSavings] = useState('');
    const [enteredDuration, setEnteredDuration] = useState('');
    const [enteredInterest, setEnteredInterest] = useState('');
    const [enteredYearlyContributions, setEnteredYearlyContributions] = useState('');

    const currentSavingsInputHandler = (event) => {
        setEnteredCurrentSavings(event.target.value);
    }
    const yearSavingsInputHandler = (event) => {
        setEnteredYearlyContributions(event.target.value);
    }
    const durationInputHandler = event => {
        setEnteredDuration(event.target.value);
    }
    const interestInputHandler = event => {
        setEnteredInterest(event.target.value);
    }

    const calculateHandler = (event) => {
        event.preventDefault();
        // Should be triggered when form is submitted
        // You might not directly want to bind it to the submit event on the form though...
    
        const yearlyData = []; // per-year results
      
        let currentSavings = +enteredCurrentSavings; // feel free to change the shape of this input object!
        const yearlyContribution = +enteredYearlyContributions; // as mentioned: feel free to change the shape...
        const expectedReturn = +enteredInterest / 100;
        const duration = +enteredDuration;
    
        // The below code calculates yearly results (total savings, interest etc)
        for (let i = 0; i < duration; i++) {
          const yearlyInterest = currentSavings * expectedReturn;
          currentSavings += yearlyInterest + yearlyContribution;
          yearlyData.push({
            // feel free to change the shape of the data pushed to the array!
            year: i + 1,
            yearlyInterest: yearlyInterest,
            totalSavings: currentSavings,
            yearlyContribution: yearlyContribution,
          });
        }
        console.log(yearlyData);
       props.onSaveData(yearlyData);
        // do something with yearlyData ...
      };

      const resetHandler= () => {
        console.log('Clicked!');
        setEnteredCurrentSavings('');
        setEnteredDuration('');
        setEnteredInterest('');
        setEnteredYearlyContributions('');
    
      }

    
    return (
        <form onSubmit={calculateHandler} className="form">
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={currentSavingsInputHandler} value={enteredCurrentSavings}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={yearSavingsInputHandler} value={enteredYearlyContributions} />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange={interestInputHandler} value={enteredInterest}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={durationInputHandler} value={enteredDuration}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt" onClick={resetHandler}>
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    )
}

export default Form;
