import React, { useState}  from "react";
import '../index.css';

function Form(props) {

    const [enteredCurrentSavings, setEnteredCurrentSavings] = useState('');
    const [enteredDuration, setEnteredDuration] = useState('');
    const [enteredInterest, setEnteredInterest] = useState('');
    const [enteredYearlySavings, setEnteredYearlySavings] = useState('');

    const currSavingsChangeHandler = (event) => {
        setEnteredCurrentSavings(event.target.value);
    }
    const yearSavingsChangeHandler = (event) => {
        setEnteredYearlySavings(event.target.value);
    }
    const durationChangeHandler = event => {
        setEnteredDuration(event.target.value);
    }
    const interestChangeHandler = event => {
        setEnteredInterest(event.target.value);
    }

    const calculateHandler = (event) => {
        event.preventDefault();
        // Should be triggered when form is submitted
        // You might not directly want to bind it to the submit event on the form though...
    
        const yearlyData = []; // per-year results

        const enteredData = {
            currentSavings: +enteredCurrentSavings,
            yearlySavings: enteredYearlySavings,
            expectedReturn: enteredInterest,
            duration: enteredDuration,
        }
        const expenseData = {
            title: enteredTitle,
            amount: +enteredAmount,
            date: new Date(enteredDate),
        };
        let currentSavings = +userInput['current-savings']; // feel free to change the shape of this input object!
        const yearlyContribution = +userInput['yearly-contribution']; // as mentioned: feel free to change the shape...
        const expectedReturn = +userInput['expected-return'] / 100;
        const duration = +userInput['duration'];
    
        // The below code calculates yearly results (total savings, interest etc)
        for (let i = 0; i < duration; i++) {
          const yearlyInterest = currentSavings * expectedReturn;
          currentSavings += yearlyInterest + yearlyContribution;
          yearlyData.push({
            // feel free to change the shape of the data pushed to the array!
            year: i + 1,
            yearlyInterest: yearlyInterest,
            savingsEndOfYear: currentSavings,
            yearlyContribution: yearlyContribution,
          });
        }
    
        // do something with yearlyData ...
      };

      const clickHandler= () => {
        console.log('Clicked!');
        setEnteredCurrentSavings('');
        setEnteredDuration('');
        setEnteredInterest('');
        setEnteredYearlySavings('');
    
      }

    
    return (
        <form onSubmit={calculateHandler} className="form">
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={currSavingsChangeHandler} value={enteredCurrentSavings}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={yearSavingsChangeHandler} value={enteredYearlySavings} />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange={interestChangeHandler} value={enteredInterest}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={durationChangeHandler} value={enteredDuration}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt" onClick={clickHandler}>
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    )
}

export default Form;
